example.code:
using Au; using System;
using System.Windows.Forms;
using Au.Util;
using Au.Types;

var f = new FormRegisterHotkey();
f.ShowDialog();

class FormRegisterHotkey :Form
{
	ARegisteredHotkey _hk1, _hk2;

	protected override void WndProc(ref Message m)
	{
		switch(m.Msg) {
		case 1: //WM_CREATE
			bool r1 = _hk1.Register(1, "Ctrl+Alt+F10", this);
			bool r2 = _hk2.Register(2, (KMod.Ctrl | KMod.Shift, KKey.D), this); //Ctrl+Shift+D
			AOutput.Write(r1, r2);
			break;
		case 2: //WM_DESTROY
			_hk1.Unregister();
			_hk2.Unregister();
			break;
		case ARegisteredHotkey.WM_HOTKEY:
			AOutput.Write(m.WParam);
			break;
		}
		base.WndProc(ref m);
	}
}

member.example:
Shows how to declare an ATreeBase-derived class, load tree of nodes from an XML file, find descendant nodes, save the tree to an XML file.

using System.Xml;

class MyTree : Au.Util.ATreeBase<MyTree>
{
	public string Name { get; set; }
	public int Id { get; private set; }
	public bool IsFolder { get; private set; }

	public MyTree(string name, int id, bool isFolder) { Name = name; Id = id; IsFolder = isFolder; }

	//XML element -> MyTree object
	MyTree(XmlReader x, MyTree parent)
	{
		if(parent == null) { //the root XML element
			if(x.Name != "example") throw new ArgumentException("XML root element must be 'example'");
			IsFolder = true;
		} else {
			switch(x.Name) {
			case "e": break;
			case "f": IsFolder = true; break;
			default: throw new ArgumentException("XML element must be 'e' or 'f'");
			}
#if true //two ways of reading attributes
			Name = x["name"];
			Id = x["id"].ToInt();
#else
			while(x.MoveToNextAttribute()) {
				var v = x.Value;
				switch(x.Name) {
				case "name": Name = v; break;
				case "id": Id = v.ToInt(); break;
				}
			}
#endif
			if(Name.NE()) throw new ArgumentException("no 'name' attribute in XML");
			if(Id == 0) throw new ArgumentException("no 'id' attribute in XML");
		}
	}

	public static MyTree Load(string file) => XmlLoad(file, (x, p) => new MyTree(x, p));

	public void Save(string file) => XmlSave(file, (x, n) => n._XmlWrite(x));

	//MyTree object -> XML element
	void _XmlWrite(XmlWriter x)
	{
		if(Parent == null) {
			x.WriteStartElement("example");
		} else {
			x.WriteStartElement(IsFolder ? "f" : "e");
			x.WriteAttributeString("name", Name);
			x.WriteAttributeString("id", Id.ToString());
		}
	}

	public override string ToString() => $"{new string(' ', Level)}{(IsFolder ? 'f' : 'e')} {Name} ({Id})";
}

static void TNodeExample()
{
	/*
	<example>
	  <e name="one" id="1" />
	  <f name="two" id="112">
		<e name="three" id="113" />
		<e name="four" id="114" />
		<f name="five" id="120">
		  <e name="six" id="121" />
		  <e name="seven" id="122" />
		</f>
	  </f>
	  <f name="eight" id="217" />
	  <e name="ten" id="144" />
	</example>
	*/

	var x = MyTree.Load(@"Q:\test\example.xml");
	foreach(MyTree n in x.Descendants(true)) AOutput.Write(n);
	//AOutput.Write(x.Descendants().FirstOrDefault(k => k.Name == "seven")); //find a descendant
	//AOutput.Write(x.Descendants().Where(k => k.Level > 2)); //find some descendants
	x.Save(@"Q:\test\example2.xml");
}

example.code:
var path = @"C:\Test\test.txt";
if (AFile.Exists(path)) AOutput.Write("exists");
if (AFile.Exists(path).isFile) AOutput.Write("exists as file");
if (AFile.Exists(path).isDir) AOutput.Write("exists as directory");
if (AFile.Exists(path) is FAttr { isFile: true, isReadonly: false }) AOutput.Write("exists as file and isn't readonly");
switch (AFile.Exists(path)) {
case 0: AOutput.Write("doesn't exist"); break;
case 1: AOutput.Write("file"); break;
case 2: AOutput.Write("dir"); break;
}

string path = @"D:\Test\new\test.txt";
AFile.CreateDirectoryFor(path);
File.WriteAllText(path, "text"); //would fail if directory @"D:\Test\new" does not exist

var s1 = File.ReadAllText(file); //unsafe. Exception if the file is locked.

var s2 = AFile.WaitIfLocked(() => File.ReadAllText(file)); //safe. Waits while the file is locked.

using(var f = AFile.WaitIfLocked(() => File.OpenText(file))) { //safe. Waits while the file is locked.
	var s3 = f.ReadToEnd();
}

using(var f = AFile.WaitIfLocked(() => File.Create(file))) { //safe. Waits while the file is locked.
	f.WriteByte(1);
}

