	.WinSaved(Microsoft.Win32.Registry.GetValue(rk, rv, null) as string, o => Microsoft.Win32.Registry.SetValue(rk, rv, o))
	.End();

.AlsoAll((b, e) => {
	if(b.Last is CheckBox c) { c.IsChecked = true; b.Margin("t1 b1"); }
})

