	AOutput.Write(x.code);
	switch(x.code) {
	case HookData.CbtEvent.ACTIVATE:
		AOutput.Write(x.Hwnd);
		break;
	case HookData.CbtEvent.CREATEWND:
		var c=x.CreationInfo->lpcs;
		AOutput.Write(x.Hwnd, c->x, c->lpszName);
