AOutput.Write(x.IsMatch(s));

var s = "one two22 three333 four";
var x = new ARegex(@"\b(\w+?)(\d+)\b");
if(x.Match(s, out var m)) AOutput.Write(m.Value, m[1].Value, m[2].Value);

var s = "one two22 three333 four";
var x = new ARegex(@"\b(\w+?)(\d+)\b");
if(x.MatchG(s, out var g)) AOutput.Write(g.Value, g.Start);

var s = "one two22 three333 four";
var x = new ARegex(@"\b(\w+?)(\d+)\b");
if(x.MatchS(s, out var v)) AOutput.Write(v);

var s = "one two22 three333 four";
var x = new ARegex(@"\b(\w+?)(\d+)\b");
foreach(var m in x.FindAll(s)) AOutput.Write(m.Value, m[1].Value, m[2].Value);

var s = "one two22 three333 four";
var x = new ARegex(@"\b(\w+?)(\d+)\b");
if(!x.FindAll(s, out var a)) { AOutput.Write("not found"); return; }
foreach(var m in a) AOutput.Write(m.Value, m[1].Value, m[2].Value);

var s = "one two three";
var x = new ARegex(@"\b\w+\b");
foreach(var g in x.FindAllG(s)) AOutput.Write(g.Start, g.Value);

var s = "one two three";
var x = new ARegex(@"\b\w+\b");
if(!x.FindAllG(s, out var a)) { AOutput.Write("not found"); return; }
foreach(var g in a) AOutput.Write(g.Start, g.Value);

var s = "one two three";
var x = new ARegex(@"\b\w+\b");
foreach(var v in x.FindAllS(s)) AOutput.Write(v);

var s = "one two three";
var x = new ARegex(@"\b\w+\b");
if(!x.FindAllS(s, out var a)) { AOutput.Write("not found"); return; }
foreach(var v in a) AOutput.Write(v);

var s = "one two22 three333 four";
var x = new ARegex(@"\b(\w+?)(\d+)\b");
s = x.Replace(s, "'$2$1'");
AOutput.Write(s);

var s = "one two22 three333 four";
var x = new ARegex(@"\b(\w+?)(\d+)\b");
if(0 == x.Replace(s, "'$2$1'", out s)) AOutput.Write("not found");
else AOutput.Write(s);

var s = "one two22 three333 four";
var x = new ARegex(@"\b(\w+?)(\d+)\b");
s = x.Replace(s, o => o.Value.Upper());
AOutput.Write(s);

var s = "one two22 three333 four";
var x = new ARegex(@"\b(\w+?)(\d+)\b");
if(0 == x.Replace(s, o => o.Value.Upper(), out s)) AOutput.Write("not found");
else AOutput.Write(s);

var s = "one, two,three , four";
var x = new ARegex(@" *, *");
var a = x.Split(s);
for(int i = 0; i < a.Length; i++) AOutput.Write(i, a[i]);

var s = "one, two,three , four";
var x = new ARegex(@" *, *");
var a = x.SplitG(s);
foreach(var v in a) AOutput.Write(v.Start, v.Value);

string s = "one * two three ";
foreach(var t in s.Segments(" ")) AOutput.Write(s[t.start..t.end]);
foreach(var t in s.Segments(SegSep.Word, SegFlags.NoEmpty)) AOutput.Write(s[t.start..t.end]);

string s = @"C:\abc\mno.xyz";
if(s.Like(@"C:\abc\mno.xyz")) AOutput.Write("matches whole text (no wildcard characters)");
if(s.Like(@"C:\abc\*")) AOutput.Write("starts with");
if(s.Like(@"*.xyz")) AOutput.Write("ends with");
if(s.Like(@"*mno*")) AOutput.Write("contains");
if(s.Like(@"C:\*.xyz")) AOutput.Write("starts and ends with");
if(s.Like(@"?:*")) AOutput.Write("any character, : and possibly more text");

//This version does not support wildcard expressions.
Document Find1(string name, string date)
{
	return Documents.Find(x => x.Name.Eqi(name) && x.Date.Eqi(date));
}

//This version supports wildcard expressions.
//null-string arguments are not compared.
Document Find2(string name, string date)
{
	AWildex n = name, d = date; //null if the string is null
	return Documents.Find(x => (n == null || n.Match(x.Name)) && (d == null || d.Match(x.Date)));
}

//Example of calling such function.
//Find item whose name is "example" (case-insensitive) and date starts with "2017-".
var item = x.Find2("example", "2017-*");

