example.code:
var perf = new APerf.Local { Incremental = true };
for(int i = 0; i < 5; i++) {
	Thread.Sleep(100); //not included in the measurement
	perf.First();
	Thread.Sleep(30); //will make sum ~150000
	perf.Next();
	Thread.Sleep(10); //will make sum ~50000
	perf.Next();
	Thread.Sleep(100); //not included in the measurement
}
perf.Write(); //speed:  154317  51060  (205377)
perf.Incremental = false;

using(var p1 = APerf.Create()) { //p1.First();
	1.ms();
	p1.Next();
	1.ms();
} //p1.NW();

APerf.Incremental = true;
for(int i = 0; i < 5; i++) {
	Thread.Sleep(100); //not included in the measurement
	APerf.First();
	Thread.Sleep(30); //will make sum ~150000
	APerf.Next();
	Thread.Sleep(10); //will make sum ~50000
	APerf.Next();
	Thread.Sleep(100); //not included in the measurement
}
APerf.Write(); //speed:  154317  51060  (205377)
APerf.Incremental = false;

APerf.First(100);
CODE1;
APerf.Next();
CODE2;
APerf.Next();
APerf.Write(); //speed:  timeOfCODE1  timeOfCODE2  (totalTime)

ref var p = ref APerf.Shared;
p.First();
//or
APerf.Shared.First();

ATime.Sleep(50);
50.ms(); //the same
0.05.s(); //the same

ATime.Sleep(5000);
5000.ms(); //the same
5.s(); //the same

ATime.Sleep(2500);
2500.ms(); //the same
2.5.s(); //the same

_Test("before");
using(new ATime.SleepPrecision_(2)) {
	_Test("in");
}
_Test("after");

void _Test(string name)
{
	AOutput.Write(name);
	APerf.First();
