Triggers.Hotkey["Ctrl+L"] = o => AOutput.Write(AOpt.Key.KeySpeed); //20
Triggers.Hotkey["Ctrl+Shift+L"] = o => AOutput.Write(AOpt.Key.KeySpeed); //20

