var r = ADialog.Show("Main text", "More text.", "1 OK|2 Cancel", expandedText: "Expanded text", controls: con, secondsTimeout: 30);
AOutput.Write(r, con.IsChecked, con.RadioId, con.EditText);
switch(r) {
case 1: AOutput.Write("OK"); break;
