var m = new AMenu();
m["One"] = o => AOutput.Write(o);
m["Two"] = o => AOutput.Write(o);
m.Submenu("Submenu");
	m["Three"] = o => AOutput.Write(o);
	m["Four"] = o => AOutput.Write(o);
	m.EndSubmenu();
m["Five"] = o => AOutput.Write(o);
m.Show();

var m = new AMenu();
m["One"] = o => AOutput.Write(o);
m["Two"] = o => AOutput.Write(o);
m.LazySubmenu("Submenu", _ => {
	AOutput.Write("adding items of " + m.CurrentAddMenu.OwnerItem);
	m["Three"] = o => AOutput.Write(o);
	m["Four"] = o => AOutput.Write(o);
	m.LazySubmenu("Submenu2", _ => {
		AOutput.Write("adding items of " + m.CurrentAddMenu.OwnerItem);
		m["Five"] = o => AOutput.Write(o);
		m["Six"] = o => AOutput.Write(o);
	});
	m["Seven"] = o => AOutput.Write(o);
});
m["Eight"] = o => AOutput.Write(o);
m.Show();

_Toolbar_Common(); //show toolbar. Don't attach to a window.
//_Toolbar_Common2(); //add function for each toolbar
Triggers.Options.ThreadMain(); //let all toolbars run in this thread
Triggers.Window[Au.Triggers.TWEvent.ActiveOnce, "*Notepad", "Notepad"] = o => _Toolbar_Notepad(o.Window); //show toolbar attached to this Notepad window
//Triggers.Window[Au.Triggers.TWEvent.ActiveOnce, "other window"] = o => _Toolbar_Other(o.Window); //add trigger and function for each toolbar
Triggers.Run();

void _Toolbar_Common() {
	var t = new AToolbar("example");
	t["button 1", @"C:\example.ico"] = o => AOutput.Write(o);
	t.MenuButton("menu", m => {
		m["item 1"] = o => AOutput.Write(o);
		m["item 2"] = o => AFile.TryRun(AFolders.System + "notepad.exe");
	}, AIcon.GetStockIcon(StockIcon.FOLDER, 16));
	t["Notepad"] = o => AFile.TryRun(AFolders.System + "notepad.exe");
	t.Show();
}

void _Toolbar_Notepad(AWnd w) {
	var t = new AToolbar("tb notepad");
	t["button 1"] = o => AOutput.Write(o);
	t.Show(w);
}

var t = new AToolbar();
t.NoText = true;
t["Find", @"Q:\images\find.ico"] = o => AOutput.Write(o);
t["Copy", @"Q:\images\copy.ico"] = o => AOutput.Write(o);
t.NoText = false;
t["Delete", @"Q:\images\delete.ico"] = o => AOutput.Write(o);
t.NoText = true;
t["Run", @"Q:\images\run.ico"] = o => AOutput.Write(o);

string path = @"D:\Test\new\test.txt";
AFile.CreateDirectoryFor(path);
File.WriteAllText(path, "text"); //would fail if directory @"D:\Test\new" does not exist

var s1 = File.ReadAllText(file); //unsafe. Exception if the file is locked.

var s2 = AFile.WaitIfLocked(() => File.ReadAllText(file)); //safe. Waits while the file is locked.

using(var f = AFile.WaitIfLocked(() => File.OpenText(file))) { //safe. Waits while the file is locked.
	var s3 = f.ReadToEnd();
}

using(var f = AFile.WaitIfLocked(() => File.Create(file))) { //safe. Waits while the file is locked.
	f.WriteByte(1);
}

