Run notepad or activate its window.

AWnd w = AWnd.FindOrRun("*- Notepad", run: () => AFile.Run("notepad.exe"));

example.code:
string v = "example";
int r1 = AFile.RunConsole(@"Q:\Test\console1.exe", $@"/an ""{v}"" /etc");

int r2 = AFile.RunConsole(s => AOutput.Write(s), @"Q:\Test\console2.exe");

int r3 = AFile.RunConsole(out var text, @"Q:\Test\console3.exe", encoding: Encoding.UTF8);
AOutput.Write(text);

//open database file
using var db = new ASqlite(@"Q:\test\sqlite.db");
//create table
db.Execute("CREATE TABLE IF NOT EXISTS test(id INTEGER PRIMARY KEY, name TEXT, x INT, guid BLOB, array BLOB)");

//add 2 rows of data
using(var trans = db.Transaction()) { //optional, but makes much faster when making multiple changes, and ensures that all or none of these changes are written to the database
	using(var p = db.Statement("INSERT OR REPLACE INTO test VALUES(?, ?, :x, ?, ?)")) {
		//assume we want to add values of these variables to the database table
		int id = 1; string name = "TEXT"; long x = -10; Guid guid = Guid.NewGuid(); int[] arr = new int[] { 1, 2, 3 };
		//add first row
		p.Bind(1, id);
		p.Bind(2, name).BindStruct(4, guid).Bind(5, arr);
		p.Bind(":x", x);
		p.Step();
		//add second row
		p.Reset().Bind(1, 2).Bind(":x", 123456789012345).Step(); //unbound columns are null
	}
	//update single row
	db.Execute("UPDATE test SET name=?2 WHERE id=?1", 2, "two");
	//write all this to database
	trans.Commit();
}

//get data
using(var p = db.Statement("SELECT * FROM test")) {
	while(p.Step()) { //for each row of results
		AOutput.Write(p.GetInt(0), p.GetText(1), p.GetLong(2));
		AOutput.Write(p.GetStruct<Guid>("guid"));
		AOutput.Write(p.GetArray<int>("array"));
		AOutput.Write("----");
	}
}
//get single value
if(db.Get(out string s1, "SELECT name FROM test WHERE id=?", 1)) AOutput.Write(s1); else AOutput.Write("not found");
if(db.Get(out int i1, "PRAGMA page_size")) AOutput.Write(i1);

string text = null; Bitmap image = null; string[] files = null;
AClipboard.CopyData(() => { text = AClipboardData.GetText(); image = AClipboardData.GetImage(); files = AClipboardData.GetFiles(); });
if(text == null) AOutput.Write("no text in clipboard"); else AOutput.Write(text);
if(image == null) AOutput.Write("no image in clipboard"); else AOutput.Write(image.Size);
if(files == null) AOutput.Write("no files in clipboard"); else AOutput.Write(files);

member.example:
Get bitmap image from clipboard.

var image = AClipboardData.GetImage();
if(image == null) AOutput.Write("no image in clipboard"); else AOutput.Write(image.Size);

Set clipboard data of two formats: text and image.

new AClipboardData().AddText("text").AddImage(Image.FromFile(@"q:\file.png")).SetClipboard();

Paste data of two formats: HTML and text.

AClipboard.PasteData(new AClipboardData().AddHtml("<b>text</b>").AddText("text"));

Copy data of two formats: HTML and text.

string html = null, text = null;
AClipboard.CopyData(() => { html = AClipboardData.GetHtml(); text = AClipboardData.GetText(); });
AOutput.Write(html); AOutput.Write(text);

example.code:
d.AddHtml("<i>italy</i>");
d.AddHtml("<html><body><i>italy</i></body></html>");
d.AddHtml("<html><body><!--StartFragment--><i>italy</i><!--EndFragment--></body></html>");

bool stop = false;
using var hook = new AHookAcc(AccEVENT.SYSTEM_FOREGROUND, 0, x => {
	AOutput.Write(x.wnd);
	var a = x.GetAcc();
	AOutput.Write(a);
	if(x.wnd.ClassNameIs("Shell_TrayWnd")) stop = true;
});
ADialog.Show("hook");
//or
//AWaitFor.MessagesAndCondition(-10, () => stop); //wait max 10 s for activated taskbar
//AOutput.Write("the end");

var stop = false;
using var hook = AHookWin.Keyboard(x => {
	AOutput.Write(x);
	if(x.vkCode == KKey.Escape) { stop = true; x.BlockEvent(); }
});
ADialog.Show("hook");
//or
//AWaitFor.MessagesAndCondition(-10, () => stop); //wait max 10 s for Esc key
//AOutput.Write("the end");

var stop = false;
using var hook = AHookWin.Mouse(x => {
	AOutput.Write(x);
	if(x.Event == HookData.MouseEvent.RightButton) { stop = x.IsButtonUp; x.BlockEvent(); }
});
ADialog.Show("hook");
//or
//AWaitFor.MessagesAndCondition(-10, () => stop); //wait max 10 s for right-click
//AOutput.Write("the end");

using var hook = AHookWin.ThreadCbt(x => {
	AOutput.Write(x.code);
	switch(x.code) {
	case HookData.CbtEvent.ACTIVATE:
		AOutput.Write(x.ActivationInfo(out _, out _));
		break;
	case HookData.CbtEvent.CREATEWND:
		AOutput.Write(x.CreationInfo(out var c, out _), c->x, c->lpszName);
		break;
	case HookData.CbtEvent.CLICKSKIPPED:
		AOutput.Write(x.MouseInfo(out var m), m->pt, m->hwnd);
		break;
	case HookData.CbtEvent.KEYSKIPPED:
		AOutput.Write(x.KeyInfo(out _));
		break;
	case HookData.CbtEvent.SETFOCUS:
		AOutput.Write(x.FocusInfo(out AWnd wPrev), wPrev);
		break;
	case HookData.CbtEvent.MOVESIZE:
		AOutput.Write(x.MoveSizeInfo(out var r), r->ToString());
		break;
	case HookData.CbtEvent.MINMAX:
		AOutput.Write(x.MinMaxInfo(out var state), state);
		break;
	case HookData.CbtEvent.DESTROYWND:
		AOutput.Write((AWnd)x.wParam);
		break;
	}
	return false;
});
ADialog.ShowOkCancel("hook");
//new Form().ShowDialog(); //to test MINMAX

using var hook = AHookWin.ThreadGetMessage(x => {
	AOutput.Write(x.msg->ToString(), x.PM_NOREMOVE);
});
ADialog.Show("hook");

using var hook = AHookWin.ThreadKeyboard(x => {
	AOutput.Write(x.key, 0 != (x.lParam & 0x80000000) ? "up" : "", x.lParam, x.PM_NOREMOVE);
	return false;
});
ADialog.Show("hook");

using var hook = AHookWin.ThreadMouse(x => {
	AOutput.Write(x.message, x.m->pt, x.m->hwnd, x.PM_NOREMOVE);
	return false;
});
ADialog.Show("hook");

using var hook = AHookWin.ThreadCallWndProc(x => {
	ref var m = ref *x.msg;
	var mm = Message.Create(m.hwnd.Handle, (int)m.message, m.wParam, m.lParam);
	AOutput.Write(mm, x.sentByOtherThread);
});
ADialog.Show("hook");

using var hook = AHookWin.ThreadCallWndProcRet(x => {
	ref var m = ref *x.msg;
	var mm = Message.Create(m.hwnd.Handle, (int)m.message, m.wParam, m.lParam); mm.Result = m.lResult;
	AOutput.Write(mm, x.sentByOtherThread);
});
ADialog.Show("hook");

